import React, { useState } from 'react';
import axios from 'axios';
import '../../App.css';

const registerUrl = 'https://84jbs45w8h.execute-api.us-east-1.amazonaws.com/prod'

// export default function SignUp() {
// 	return <ul className='sign-up'>
// 	<h1>pagina teste 4</h1>
// 	<h3> ainda esta em construção</h3>
// 	</ul>
// }
const SignUp = () => {

	const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
	const [password, setPassword] = useState('');
	const [message, setMessage] = useState(null);

const submitHandler = (event) => {
	event.preventDefault();
	if (username.trim() === '' || email.trim() === '' || name.trim() === '' || password.trim() === '') {
		setMessage('All fields are required');
		return;
	}
	console.log('submit buttom is pressed')
}

	return (
		<ul className='sign-up'>
		<div>
			<form onSubmit={submitHandler}>
				<h3>Register</h3>
				name: <input type="text" value={name} onChange={event => setName(event.target.value)} /><br/>
				email: <input type="text" value={email} onChange={event => setEmail(event.target.value)} /> <br/>
				username: <input type="text" value={username} onChange={event => setUsername(event.target.value)} /> <br/>
				password: <input type="text" value={password} onChange={event => setPassword(event.target.value)} /> <br/>
				<input type="submit" value="Register" />
			</form>
			{message && <p className="message">{message}</p>}
		</div>
		</ul>
	)
}

export default SignUp;
